function StranText(txt,toFT,chgTxt)
{
	if(txt==""||txt==null)return ""
	toFT=toFT==null?BodyIsFt:toFT
	if(chgTxt)txt=txt.replace((toFT?"¼ò":"·±"),(toFT?"·±":"¼ò"))
	if(toFT){return Traditionalized(txt)}
	else {return Simplized(txt)}
}
