function StranBody(fobj)
{
	if(typeof(fobj)=="object"){var obj=fobj.childNodes}
	else 
	{
		var tmptxt=StranLink_Obj.innerHTML.toString()
		if(tmptxt.indexOf("¼ò")<0)
		{
			BodyIsFt=1
			StranLink_Obj.innerHTML=StranText(tmptxt,0,1)
			StranLink_Obj.title=StranText(StranLink_Obj.title,0,1)
		}
		else
		{
			BodyIsFt=0
			StranLink_Obj.innerHTML=StranText(tmptxt,1,1)
			StranLink_Obj.title=StranText(StranLink_Obj.title,1,1)
		}
		setCookie(JF_cn,BodyIsFt,7)
		var obj=document.body.childNodes
	}
	for(var i=0;i<obj.length;i++)
	{
		var OO=obj.item(i)
		if("|BR|HR|TEXTAREA|SCRIPT|OBJECT|EMBED|".indexOf("|"+OO.tagName+"|")!=-1||OO==StranLink_Obj){
			continue;
		}		
		if(OO.title!=""&&OO.title!=null){
			OO.title=StranText(OO.title);
		}
		if(OO.alt!=""&&OO.alt!=null){
			OO.alt=StranText(OO.alt);
		}
		if(OO.tagName=="INPUT"&&OO.value!=""&&OO.type!="text"&&OO.type!="hidden"){
			OO.value=StranText(OO.value);
		}
		if(OO.nodeType==3){
			OO.data=StranText(OO.data)
		}
		else StranBody(OO)
	}
}
